import dev.s7a.animotion.converter.util.path.saveImage
import okio.Path.Companion.toPath
import util.assertFileContent
import util.path.tempFile
import kotlin.test.Test

class SaveImageTest {
    @Test
    fun saveImage() {
        val base64 = "data:image/png;base64,"
        val file = tempFile(suffix = ".png")
        file.saveImage(base64)
        assertFileContent("src/commonTest/resources/robit.png".toPath(), file)
    }
}
